<?php	header("Content-Type: application/json");	include("../conexi.php");		$response= array();	$link=Conectarse();		$myusername=$_POST['id_usuarios'];	$mypassword=$_POST['password']; 		$myusername = stripslashes($myusername);	$mypassword = stripslashes($mypassword);	$sql="SELECT	id_usuarios,	nombre_usuarios,	permisos		FROM usuarios 	WHERE id_usuarios='$myusername' 	AND pass_usuarios='$mypassword'";			$result=mysqli_query($link, $sql);	if (!$result){		die('Error: ' . mysqli_error($link));	}	$count=mysqli_num_rows($result);		// Si la consulta devuelve 1 fila inicia la sesion	if($count==1){		session_start();		session_regenerate_id(true);		$id_sesion = session_id();		$row = mysqli_fetch_assoc($result);		$id_usuario = $row["id_usuarios"];						setcookie("id_usuarios", $id_usuario,  0, "/");		setcookie("permiso_usuarios", $row["permisos"],  0, "/");		setcookie("nombre_usuarios", $row["nombre_usuarios"],  0, "/");						$response["login"] = "valid";		$response["permisos"] = $row["permisos"];			}	else{		$response["login"] = "invalid";		$response["query"] = $sql;		$response["mensaje"] = "Usuario y/o Contraseña Inválidos";	}	echo json_encode($response);?>